from collections import deque

from pympler.asizeof import asizeof


__author__ = 'Zhang Jinsong'


class SpiderCache(deque):
    """
    Spider 缓存类，将各对象大小之和作为当前缓存大小。
    """

    def __sizeof__(self):
        return sum(map(asizeof, self))
